import './App.css';
import React, {Fragment} from 'react';
import {Table} from './components/table/table';
import {AddContactButton} from './components/add_contact_button/add_contact_button';

class App extends React.Component {
    state = {
        contacts: [],
    }

    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleSubmit(contact) {
        let addedContact = this.state.contacts.push(contact);
        this.setState({
            contacts: addedContact,
        });
    }

    render() {
        return (
            <Fragment>
                <Table contacts={this.state.contacts} />
                <AddContactButton onFormSubmit={this.handleSubmit} />
            </Fragment>
        );
    }
}

export default App;
