import React from 'react';
import { Formik, Field, Form } from "formik";
import './form.css';

export class FormContact extends React.Component {
    constructor(props) {
        super(props);
        this.submit = this.submit.bind(this);
        this.cancel = this.cancel.bind(this);
        this.state = {
            contact: {
                name: '',
                surname: '',
                phone: '',
            }
        }
    }

    submit() {
        this.state.contacts.push()
    }

    cancel() {

    }

    render() {
        return (
            <Formik
                initialValues={{ contact_name: '', contact_surname: '', contact_phone: '', }}
            >
                <Form className='form-contact'>
                    <legend className='form-legend'>Данные контакта</legend>
                    <label htmlFor='contact-name'>Имя</label>
                    <Field type='text' id='contact-name' name='name' pattern='[A-Za-zА-Яа-я]+' required/>
                    <label htmlFor='contact-surname'>Фамилия</label>
                    <Field type='text' id='contact-surname' name='surname' pattern='[A-Za-zА-Яа-я]+' required/>
                    <label htmlFor='contact-phone'>Телефон</label>
                    <Field type='tel' id='contact-phone' name='phone' pattern='[0-9]+' required/>
                    <div className='form-controls'>
                        <button className='button-save' onClick={this.submit}>Сохранить</button>
                        <button className='button-cancel' onClick={this.cancel}>Отмена</button>
                    </div>
                </Form>
            </Formik>
        );
    }
}
