import React, {Fragment} from 'react';
import './add_contact_button.css';
import {Form} from '../form/form';

export class AddContactButton extends React.Component {
    state = {
        isShowForm: false,
    }

    constructor(props) {
        super(props);
        this.toggleForm = this.toggleForm.bind(this);

        this.handleCancel = this.handleCancel.bind(this);
    }

    handleCancel() {
        this.setState({
            isShowForm: false,
        })
    }

    render() {
        const {isShowForm} = this.state;

        const form = isShowForm && <Form onFormCancel={this.handleCancel} />

        return (
            <Fragment>
                <button className='add-contact' onClick={this.toggleForm}>
                    Добавить контакт
                </button>
                {form}
            </Fragment>
        );
    }

    toggleForm() {
        this.setState({
            isShowForm: !this.state.isShowForm,
        })
    }
}
