import React from 'react';
import './add_button.css';

export class AddContactButton extends React.Component {
    state = {
        isShowForm: false
    }

    constructor(props) {
        super(props);
        this.openForm = this.openForm.bind(this);
    }

    render() {
        const {isShowForm} = this.state;

        return (
            <button className='add-contact' onClick={this.openForm}>
                Добавить контакт
            </button>
        );
    }

    openForm() {
        this.setState({
            isShowForm: !this.state.isShowForm
        })
    }
}
