import React from 'react';
import './form.css';

export class Form extends React.Component {
    constructor(props) {
        super(props);
        this.submit = this.submit.bind(this);
        this.cancel = this.cancel.bind(this);
        this.state = {
            contact: {
                name: '',
                surname: '',
                phone: '',
            }
        }
    }

    submit() {
        this.state.contacts.push(this.state.contact);
        console.log(this.state.contacts);
    }

    cancel() {

    }

    render() {
        return (
            <div className='form-contact'>
                <span className='form-legend'>Данные контакта</span>
                <label htmlFor='contact-name'>Имя</label>
                <input type='text' id='contact-name' name='name' pattern='[A-Za-zА-Яа-я]+'
                       value={this.state.contact.name} required/>
                <label htmlFor='contact-surname'>Фамилия</label>
                <input type='text' id='contact-surname' name='surname' pattern='[A-Za-zА-Яа-я]+' required/>
                <label htmlFor='contact-phone'>Телефон</label>
                <input type='tel' id='contact-phone' name='phone' pattern='[0-9]+' required/>
                <div className='form-controls'>
                    <button className='button-save' onClick={this.submit}>Сохранить</button>
                    <button className='button-cancel' onClick={this.cancel}>Отмена</button>
                </div>
            </div>
        );
    }
}
